mapper.select:
select
id, username, password, avatar, phone, email, age, sex, address, create_time, update_time
from user


        and id = #{id}


        and username = #{username}


        and password = #{password}


        and avatar = #{avatar}


        and phone = #{phone}


        and email = #{email}


        and age = #{age}


        and sex = #{sex}


        and address = #{address}


        and create_time = #{createTime}


        and update_time = #{updateTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from user


        and id = #{id}


        and username = #{username}


        and password = #{password}


        and avatar = #{avatar}


        and phone = #{phone}


        and email = #{email}


        and age = #{age}


        and sex = #{sex}


        and address = #{address}


        and create_time = #{createTime}


        and update_time = #{updateTime}
